<body>

<div id="content" class="wm180">
    <h1>弔　事　連　絡　表</h1>
    <table class="t1 wm72">
        <tr class="l1">
            <th class="w25"><div>受付日</div></th>
            <td class="w75"><div>{{$val['reception_datetime']}}</div></td>
        </tr>
    </table>
    <table class="t2">
        <tr>
            <th class="w10 l1"><div>フリガナ</div></th>
            <td class="w30"><div>{{$data['related_kana']}}</div></td>
            <th class="w30"><div>社員区分</div></th>
            <th class="w30"><div>入社年度</div></th>
        </tr>
        <tr>
            <th class="l2"><div>社員氏名</div></th>
            <td><div class="f10">{{$data['related_name']}}</div></td>
            <td><div>{{$val['classification']}}</div></td>
            <td><div>{{$data['membership_year']}} 年</div></td>
        </tr>
        <tr>
            <th class="l1" colspan="2"><div>就業会社</div></th>
            <th><div>所属（店・部・事業所）</div></th>
            <th><div>所属（ショップ・課・室・担当）</div></th>
        </tr>
        <tr>
            <td class="l2" colspan="2"><div>{{$val['company']}}</div></td>
            <td><div>{{$data['member1']}}</div></td>
            <td><div>{{$data['member2']}}</div></td>
        </tr>
    </table>
    <table class="t3">
        <tr>
            <th class="l1" style><div>フリガナ</div></th>
            <td><div>{{$data['passed_away_kana']}}</div></td>
            <th><div>社員との関係</div></th>
            <th><div>逝去日</div></th>
        </tr>
        <tr class="h30">
            <th class="w10 l2"><div>個人氏名</div></th>
            <td class="w30"><div class="f10">{{$data['passed_away_name']}}</div></td>
            <td class="w30"><div>{{$val['passed_away_relationship']}}</div></td>
            <td class="w30"><div>{{$val['passed_away_date']}}</div></td>
        </tr>
    </table>
    <div class="col2">
        <div class="tr">
            <div class="col left">
                <table>
                    <tr>
                        <th class="tate" rowspan="9">通<br>夜</th>
                        <th class="w15 l2"><div>日付</div></th>
                        <td><div>@if((int)$data['wake'] == 1)執り行いません@else{{$val['wake_date']}}@endif</div></td>
                    </tr>
                    <tr>
                        <th class="l2"><div>時間</div></th>
                        <td><div>@if((int)$data['wake'] == 1) ー @else{{$data['wake_time_start']}}〜{{$data['wake_time_end']}}@endif</div></td>
                    </tr>
                    <tr>
                        <th class="l2"><div>式場名</div></th>
                        <td><div>@if((int)$data['wake'] == 1) ー @else{{$data['wake_ceremony']}}@endif</div></td>
                    </tr>
                    <tr>
                        <th class="l3"><div>住所</div></th>
                        <td>@if((int)$data['wake'] == 1) <div>ー</div> @else<div class="w-break l3">〒{{$data['wake_ceremony_zip']}}<br>{{$data['wake_ceremony_addr1']}}{{$data['wake_ceremony_addr2']}}</div>@endif</td>
                    </tr>
                    <tr>
                        <th class="l2"><div>電話番号</div></th>
                        <td><div>@if((int)$data['wake'] == 1) ー @else{{$data['wake_ceremony_tel']}}@endif</div></td>
                    </tr>
                    <tr>
                        <th class="l4"><div>交通手段</div></th>
                        <td>@if((int)$data['wake'] == 1) <div>ー</div> @else<div class="w-break l4">{{$data['wake_ceremony_access']}}</div>@endif</td>
                    </tr>
                    <tr>
                        <th rowspan="3">参列者</th>
                        <td class="l2"><div>{{$data['wake_attendees1']}}</div></td>
                    </tr>
                    <tr>
                        <td class="l2"><div>{{$data['wake_attendees2']}}</div></td>
                    </tr>
                    <tr>
                        <td class="l2"><div>{{$data['wake_attendees3']}}</div></td>
                    </tr>
                </table>
            </div>
            <div class="col right">
                <table>
                    <tr>
                        <th class="tate" rowspan="9">告<br>別<br>式</th>
                        <th class="w15 l2"><div>日付</div></th>
                        <td><div>@if((int)$data['funeral'] == 1)執り行いません@else{{$val['funeral_date']}}@endif</div></td>
                    </tr>
                    <tr>
                        <th class="l2"><div>時間</div></th>
                        <td><div>@if((int)$data['funeral'] == 1) ー @else{{$data['funeral_time_start']}}〜{{$data['funeral_time_end']}}@endif</div></td>
                    </tr>
                    <tr>
                        <th class="l2"><div>式場名</div></th>
                        <td><div>@if((int)$data['funeral'] == 1) ー @elseif((int)$data['funeral_same_ceremony'] == 0)通夜と同じ@else{{$data['funeral_ceremony']}}@endif</div></td>
                    </tr>
                    <tr>
                        <th class="l3"><div>住所</div></th>
                        <td>@if((int)$data['funeral'] == 1) <div>ー</div> @elseif((int)$data['funeral_same_ceremony'] == 0) <div>通夜と同じ</div> @else <div class="w-break l3">〒{{$data['funeral_ceremony_zip']}}<br>{{$data['funeral_ceremony_addr1']}}{{$data['uneral_ceremony_addr2']}}</div> @endif</td>
                    </tr>
                    <tr>
                        <th class="l2"><div>電話番号</div></th>
                        <td><div>@if((int)$data['funeral'] == 1) ー @elseif((int)$data['funeral_same_ceremony'] == 0)通夜と同じ@else{{$data['funeral_ceremony_tel']}}@endif</div></td>
                    </tr>
                    <tr>
                        <th class="l4"><div>交通手段</div></th>
                        <td>@if((int)$data['funeral'] == 1) <div>ー</div> @elseif((int)$data['funeral_same_ceremony'] == 0) <div>&nbsp;<br>通夜と同じ<br>&nbsp;</div>@else <div class="w-break l4">{{$data['funeral_ceremony_access']}}</div> @endif</td>
                    </tr>
                    <tr>
                        <th rowspan="3">参列者</th>
                        <td class="l2"><div class="ellipsis">{{$data['funeral_attendees1']}}</div></td>
                    </tr>
                    <tr>
                        <td class="l2"><div>{{$data['funeral_attendees2']}}</div></td>
                    </tr>
                    <tr>
                        <td class="l2"><div>{{$data['funeral_attendees3']}}</div></td>
                    </tr>
                </table>
            </div>
        </div>
    </div>
